import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, MenuItem, Select, FormControl, InputLabel, Typography } from '@mui/material';

function CategorySearchForTipsPage() {
    const [category, setCategory] = useState('');
    const navigate = useNavigate(); // Replace useHistory with useNavigate

    const handleNavigate = () => {
        navigate(`/top_business_tips/${category}`); // Update the route to match the one in App.js
    };

    return (
        <div style={{ padding: 20 }}>
            <Typography variant="h4" gutterBottom>
                Select a Category
            </Typography>
            <FormControl variant="outlined" style={{ minWidth: 200, marginBottom: 20 }}>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                    labelId="category-label"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    label="Category"
                >
                    <MenuItem value="restaurants">Restaurants</MenuItem>
                    <MenuItem value="bars">Bars</MenuItem>
                    <MenuItem value="cafes">Cafes</MenuItem>
                    <MenuItem value="gyms">Gyms</MenuItem>
                    <MenuItem value="salons">Salons</MenuItem>
                    {/* Add more categories as needed */}
                </Select>
            </FormControl>
            <Button onClick={handleNavigate} variant="contained" color="primary" size="large">
                View Tips
            </Button>
        </div>
    );
}

export default CategorySearchForTipsPage;
