import React, { useEffect, useState } from 'react';
import './ActiveUsers.css'; // Import CSS for animations

const ActiveUsers = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const config = require('../config.json');

    useEffect(() => {
        fetch(`http://${config.server_host}:${config.server_port}/active_users`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`Network response was not ok: ${response.statusText}`);
                }
                return response.json();
            })
            .then(data => {
                setUsers(data);
                setLoading(false);
            })
            .catch(err => {
                console.error('Failed to fetch active users:', err);
                setError(`Failed to load data: ${err.message}`);
                setLoading(false);
            });
    }, []);

    if (loading) return <div className="loader">Loading...</div>;
    if (error) return <div className="error">Error loading active users: {error}</div>;
    if (!users.length) return <div>No active users found!</div>;

    return (
        <div className="user-list">
            <h2>Active Users</h2>
            {users.map(user => (
                <div key={user.user_id} className="user-entry">
                    {user.name} - Reviews: {user.review_count}
                </div>
            ))}
        </div>
    );
};

export default ActiveUsers;
