import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams

const UserPage = () => {
    const { userId } = useParams(); // Destructure userId from useParams
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const config = require('../config.json');

    useEffect(() => {
        fetch(`http://${config.server_host}:${config.server_port}/user/${userId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                setUser(data);
                setLoading(false);
            })
            .catch(error => {
                console.error("Failed to fetch user:", error);
                setError(`Failed to load user data: ${error.message}`);
                setLoading(false);
            });
    }, [userId]); // useEffect dependency on userId

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!user) return <div>No user found.</div>;

    return (
        <div>
            <h1>User Details</h1>
            <div><strong>Name:</strong> {user.name}</div>
            <div><strong>User ID:</strong> {userID}</div>
            <div><strong>Review Count:</strong> {user.review_count}</div>
        </div>
    );
};

export default UserPage;
