import { useHistory } from 'react-router-dom'; // Import useHistory hook from react-router-dom

const UserPage = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const config = require('../config.json');
    const history = useHistory(); // Hook for programmatically navigating
    const { userId } = useParams();

    useEffect(() => {
        fetch(`http://${config.server_host}:${config.server_port}/user/${userId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                setUser(data);
                setLoading(false);
                if (!data.friends || data.friends.trim() === '') {
                    // Assuming you know the next userId or have a way to calculate it
                    history.push(`/user/${calculateNextUserId(userId)}`);
                }
            })
            .catch(error => {
                console.error("Failed to fetch user:", error);
                setError("Failed to load user data");
                setLoading(false);
            });
    }, [userId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!user) return <div>No user found.</div>;

