import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom'; // Import useNavigate and useParams

const UserPage = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const config = require('../config.json');
    const navigate = useNavigate(); // useNavigate hook
    const { userId } = useParams();

    useEffect(() => {
        fetch(`http://${config.server_host}:${config.server_port}/user/${userId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                setUser(data);
                setLoading(false);
                if (!data.friends || data.friends.trim() === '') {
                    // Assuming you know the next userId or have a way to calculate it
                    navigate(`/user/${calculateNextUserId(userId)}`);
                }
            })
            .catch(error => {
                console.error("Failed to fetch user:", error);
                setError("Failed to load user data");
                setLoading(false);
            });
    }, [userId, navigate]); // Adding navigate as a dependency

    // Define this function based on how you determine the next user ID
    function calculateNextUserId(currentUserId) {
        // This is a placeholder function
        return parseInt(currentUserId) + 1; // Example increment
    }

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!user) return <div>No user found.</div>;

    return (
        <div>
            <h1>User Details</h1>
            {/* User details here */}
        </div>
    );
};

export default UserPage;
