import React from 'react';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

export default function BusinessInfoPage() {
    const { business_id } = useParams();
    const [business, setBusiness] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch(`http://${config.server_host}:${config.server_port}/business/${business_id}`)
        .then(res => res.json())
        .then(
            resJson => {
                setBusiness(resJson);
                setLoading(false);
            },
            error => {
                setError(error);
                setLoading(false);
            }
        );
    }, [business_id]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading business information!</p>;
    if (!business) return <p>No business found!</p>;

    return (
        <div>
            <h1>{business.name}</h1>
            <p>Address: {business.address}, {business.city}, {business.postal_code}</p>
            {/* Render other business information */}
        </div>
    );
}




